var mysql = require('mysql');
var result = {};

module.exports = function() {
	var connection = mysql.createConnection({
		host: 'localhost',
		port: 3306,
		user: 'root',
		password: '123',
		database: 'testdemoproject'
	})
	
	connection.connect();
	
	connection.query('select * from game',function(error, results) {
		result.resultset = results;
		// console.log(result);
	})
	connection.end();
	return result;
}